function Clock(a,b){this.tempo=a;this.audioContext=null;this.isPlaying=false;this.startTime;this.lookahead=10;this.scheduleAheadTime=0.1;this.nextNoteTime=0;this.notesCounter=0;this.timerWorker=null;this.noteOn=1;this.secondsPerNote=15/this.tempo;this.gateLength=b*this.secondsPerNote;this.newGateLength=this.gateLength;this.newTempo=this.tempo}MicroEvent.mixin(Clock);Clock.prototype.setTempo=function(a){this.newTempo=a};Clock.prototype.setGateLength=function(a){this.newGateLength=Math.min(a,0.9)*this.secondsPerNote};Clock.prototype.nextNote=function(){if(this.noteOn===1){this.nextNoteTime+=this.gateLength;this.notesCounter++;if(this.notesCounter===256){this.notesCounter=0}}else{this.nextNoteTime+=this.secondsPerNote-this.gateLength;if(this.tempo!=this.newTempo){this.tempo=this.newTempo;this.secondsPerNote=15/this.tempo}if(this.gateLength!=this.newGateLength){this.gateLength=this.newGateLength}}this.noteOn=1-this.noteOn};Clock.prototype.scheduleNote=function(b){var a=this;if(this.noteOn===1){a.trigger("tick",this.notesCounter)}else{a.trigger("note-off","Note Off")}};Clock.prototype.scheduler=function(){while(this.nextNoteTime<this.audioContext.currentTime+this.scheduleAheadTime){this.scheduleNote(this.nextNoteTime);this.nextNote()}};Clock.prototype.play=function(){this.nextNoteTime=this.audioContext.currentTime;this.notesCounter=0;this.timerWorker.postMessage("start");console.log("Starting clock");this.isPlaying=true};Clock.prototype.stop=function(){this.timerWorker.postMessage("stop");console.log("Stopping clock");this.isPlaying=false};Clock.prototype.init=function(){var a=this;this.audioContext=new AudioContext();var b=this.audioContext.createOscillator();this.timerWorker=new Worker("js/lib/Clock/clockworker.min.js");this.timerWorker.addEventListener("message",function(c){if(c.data=="tick"){a.scheduler()}else{console.log("message: "+c.data)}},false);this.timerWorker.postMessage({interval:this.lookahead})};